import Link from 'next/link'

interface CTAProps {
  headline: string
  description: string
  ctaText: string
  ctaHref: string
  backgroundColor?: string
}

export default function CTA({ headline, description, ctaText, ctaHref, backgroundColor = 'bg-gray-50' }: CTAProps) {
  return (
    <section className={`py-20 ${backgroundColor}`}>
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <h2 className="text-3xl sm:text-4xl font-bold text-gray-900 mb-6">
          {headline}
        </h2>
        <p className="text-xl text-gray-600 mb-8 max-w-2xl mx-auto">
          {description}
        </p>
        <Link
          href={ctaHref}
          className="bg-blue-600 text-white px-8 py-4 rounded-lg text-lg font-medium hover:bg-blue-700 transition-colors inline-block"
        >
          {ctaText}
        </Link>
      </div>
    </section>
  )
}

import Link from 'next/link'
import { ArrowRight, Sparkles } from 'lucide-react'

interface CTAProps {
  headline: string
  description: string
  primaryCta: { text: string; href: string }
  secondaryCta?: { text: string; href: string }
  features?: string[]
}

export default function CTA({ headline, description, primaryCta, secondaryCta, features }: CTAProps) {
  return (
    <section className="py-20 bg-gradient-to-br from-blue-600 via-purple-600 to-blue-800 relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-blue-600/20 to-purple-600/20 animate-pulse"></div>
      <div className="relative z-10 max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <div className="flex justify-center mb-6">
          <Sparkles className="h-12 w-12 text-yellow-400" />
        </div>
        <h2 className="text-3xl sm:text-4xl font-bold text-white mb-6">
          {headline}
        </h2>
        <p className="text-xl text-blue-100 mb-8 max-w-2xl mx-auto">
          {description}
        </p>

        {features && (
          <div className="flex flex-wrap justify-center gap-4 mb-8">
            {features.map((feature, index) => (
              <div key={index} className="bg-white/10 text-white px-4 py-2 rounded-full text-sm">
                {feature}
              </div>
            ))}
          </div>
        )}

        <div className="flex flex-col sm:flex-row gap-4 justify-center">
          <Link
            href={primaryCta.href}
            className="bg-white text-blue-600 px-8 py-4 rounded-lg text-lg font-medium hover:bg-gray-100 transition-colors inline-flex items-center justify-center"
          >
            {primaryCta.text}
            <ArrowRight className="ml-2 h-5 w-5" />
          </Link>
          {secondaryCta && (
            <Link
              href={secondaryCta.href}
              className="border-2 border-white text-white px-8 py-4 rounded-lg text-lg font-medium hover:bg-white hover:text-blue-600 transition-colors inline-flex items-center justify-center"
            >
              {secondaryCta.text}
            </Link>
          )}
        </div>
      </div>
    </section>
  )
}

'use client'

import { useState, useEffect } from 'react'
import Link from 'next/link'
import { Clock, Zap, Users, ArrowRight } from 'lucide-react'

interface CTAProps {
  headline: string
  description: string
  offer: string
  originalPrice?: string
  salePrice: string
  ctaText: string
  ctaHref: string
  urgencyText: string
  socialProof?: string
  endDate: string
}

export default function CTA({ 
  headline, 
  description, 
  offer, 
  originalPrice, 
  salePrice, 
  ctaText, 
  ctaHref, 
  urgencyText,
  socialProof,
  endDate
}: CTAProps) {
  const [timeLeft, setTimeLeft] = useState({ days: 0, hours: 0, minutes: 0, seconds: 0 })

  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date().getTime()
      const end = new Date(endDate).getTime()
      const difference = end - now

      if (difference > 0) {
        setTimeLeft({
          days: Math.floor(difference / (1000 * 60 * 60 * 24)),
          hours: Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((difference % (1000 * 60)) / 1000)
        })
      }
    }, 1000)

    return () => clearInterval(timer)
  }, [endDate])

  return (
    <section className="py-20 bg-gradient-to-r from-red-600 to-orange-600 relative overflow-hidden">
      <div className="absolute inset-0 bg-black/10"></div>
      <div className="relative z-10 max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <div className="bg-yellow-400 text-black px-4 py-2 rounded-full inline-block mb-6">
          <div className="flex items-center">
            <Zap className="h-4 w-4 mr-2" />
            <span className="font-bold text-sm uppercase">{urgencyText}</span>
          </div>
        </div>

        <h2 className="text-3xl sm:text-4xl font-bold text-white mb-4">
          {headline}
        </h2>
        <p className="text-xl text-red-100 mb-6">
          {description}
        </p>

        <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6 mb-8">
          <div className="text-white mb-4">
            <span className="text-2xl font-bold">{offer}</span>
          </div>

          <div className="flex items-center justify-center gap-4 mb-6">
            {originalPrice && (
              <span className="text-red-200 line-through text-xl">{originalPrice}</span>
            )}
            <span className="text-white text-3xl font-bold">{salePrice}</span>
          </div>

          {/* Countdown Timer */}
          <div className="grid grid-cols-4 gap-4 max-w-md mx-auto mb-6">
            {Object.entries(timeLeft).map(([unit, value]) => (
              <div key={unit} className="bg-white/20 rounded-lg p-3">
                <div className="text-2xl font-bold text-white">{value}</div>
                <div className="text-red-100 text-xs uppercase">{unit}</div>
              </div>
            ))}
          </div>

          <div className="flex items-center justify-center text-red-100 mb-4">
            <Clock className="h-4 w-4 mr-2" />
            <span className="text-sm">Offer expires soon!</span>
          </div>
        </div>

        {socialProof && (
          <div className="flex items-center justify-center text-red-100 mb-6">
            <Users className="h-4 w-4 mr-2" />
            <span className="text-sm">{socialProof}</span>
          </div>
        )}

        <Link
          href={ctaHref}
          className="bg-yellow-400 text-black px-8 py-4 rounded-lg text-lg font-bold hover:bg-yellow-300 transition-colors inline-flex items-center animate-pulse"
        >
          {ctaText}
          <ArrowRight className="ml-2 h-5 w-5" />
        </Link>
      </div>
    </section>
  )
}
