import { Check, Shield, Zap, Users, Globe, Award } from 'lucide-react'

interface FeaturesProps {
  headline: string
  description: string
  features: Array<{
    icon: string
    title: string
    description: string
  }>
}

const iconMap = {
  check: Check,
  shield: Shield,
  zap: Zap,
  users: Users,
  globe: Globe,
  award: Award
}

export default function Features({ headline, description, features }: FeaturesProps) {
  return (
    <section className="py-20 bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-16">
          <h2 className="text-3xl sm:text-4xl font-bold text-gray-900 mb-4">
            {headline}
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            {description}
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {features.map((feature, index) => {
            const IconComponent = iconMap[feature.icon as keyof typeof iconMap] || Check
            return (
              <div key={index} className="bg-white p-6 rounded-lg shadow-sm hover:shadow-md transition-shadow">
                <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mb-4">
                  <IconComponent className="h-6 w-6 text-blue-600" />
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                  {feature.title}
                </h3>
                <p className="text-gray-600">
                  {feature.description}
                </p>
              </div>
            )
          })}
        </div>
      </div>
    </section>
  )
}

import Image from 'next/image'
import { ArrowRight } from 'lucide-react'

interface FeaturesProps {
  headline: string
  description: string
  features: Array<{
    title: string
    description: string
    image: string
    benefits: string[]
  }>
}

export default function Features({ headline, description, features }: FeaturesProps) {
  return (
