            {headline}
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto mb-8">
            {description}
          </p>

          {/* Credentials */}
          <div className="flex flex-wrap justify-center gap-3">
            {credentials.map((credential, index) => (
              <span key={index} className="bg-blue-100 text-blue-800 text-sm px-3 py-1 rounded-full">
                {credential}
              </span>
            ))}
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {features.map((feature, index) => {
            const IconComponent = iconMap[feature.icon as keyof typeof iconMap] || Shield
            return (
              <div key={index} className="bg-white p-6 rounded-lg shadow-sm border border-slate-200">
                <div className="flex items-center mb-4">
                  <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mr-4">
                    <IconComponent className="h-6 w-6 text-blue-600" />
                  </div>
                  {feature.metric && (
                    <div className="text-right">
                      <div className="text-2xl font-bold text-blue-600">{feature.metric}</div>
                    </div>
                  )}
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">
                  {feature.title}
                </h3>
                <p className="text-gray-600">
                  {feature.description}
                </p>
              </div>
            )
          })}
        </div>

        <div className="text-center mt-12">
          <div className="inline-flex items-center text-green-600">
            <Shield className="h-5 w-5 mr-2" />
            <span className="font-semibold">Licensed, Bonded & Insured Professional Services</span>
          </div>
        </div>
      </div>
    </section>
  )
}

