  const [isMenuOpen, setIsMenuOpen] = useState(false)

  // Component data - will be populated by AI
  const data = {
    logo: "COMPANY_NAME",
    navigation: [
      { label: "Home", href: "/" },
      { label: "About", href: "/about" },
      { label: "Services", href: "/services" },
      { label: "Contact", href: "/contact" }
    ],
    ctaText: "Get Started",
    ctaHref: "/contact"
