'use client'

import Link from 'next/link'
import { useState } from 'react'
import { Search, Instagram, Twitter, Dribbble, Menu, X } from 'lucide-react'

export default function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false)

  // Component data - will be populated by AI
  const data = {
    logo: "COMPANY_NAME",
    navigation: [
      { label: "Home", href: "/" },
      { label: "Portfolio", href: "/portfolio" },
      { label: "About", href: "/about" },
      { label: "Contact", href: "/contact" }
    ],
    socialLinks: [
      { platform: "instagram", href: "https://instagram.com/company" },
      { platform: "twitter", href: "https://twitter.com/company" },
      { platform: "dribbble", href: "https://dribbble.com/company" }
    ]
  }

  const getSocialIcon = (platform: string) => {
    switch (platform.toLowerCase()) {
      case 'instagram': return <Instagram className="h-5 w-5" />
      case 'twitter': return <Twitter className="h-5 w-5" />
      case 'dribbble': return <Dribbble className="h-5 w-5" />
      default: return null
    }
  }

  return (
    <header className="bg-white">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex flex-col items-center py-6">
          <Link href="/" className="text-3xl font-bold text-gray-900 mb-4">
            {data.logo}
          </Link>

          <nav className="hidden md:flex space-x-8 mb-4">
            {data.navigation.map((item) => (
              <Link
                key={item.href}
                href={item.href}
                className="text-gray-600 hover:text-gray-900 px-3 py-2 text-sm font-medium uppercase tracking-wide"
              >
                {item.label}
              </Link>
            ))}
          </nav>

          <div className="hidden md:flex items-center space-x-4">
            {data.socialLinks.map((social) => (
              <Link
                key={social.platform}
                href={social.href}
                className="text-gray-600 hover:text-gray-900"
                target="_blank"
                rel="noopener noreferrer"
              >
                {getSocialIcon(social.platform)}
              </Link>
            ))}
            <button className="text-gray-600 hover:text-gray-900">
              <Search className="h-5 w-5" />
            </button>
          </div>

          <div className="md:hidden">
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="text-gray-700 hover:text-gray-900"
            >
              {isMenuOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
            </button>
          </div>
        </div>

        {isMenuOpen && (
          <div className="md:hidden pb-4">
            <div className="flex flex-col items-center space-y-2">
              {data.navigation.map((item) => (
                <Link
                  key={item.href}
                  href={item.href}
                  className="text-gray-600 hover:text-gray-900 px-3 py-2 text-sm font-medium uppercase tracking-wide"
                  onClick={() => setIsMenuOpen(false)}
                >
                  {item.label}
                </Link>
              ))}
            </div>
          </div>
        )}
      </div>
    </header>
  )
}
