brutalist_nav.template:
"use client"

import React, { useState } from 'react'
import { ComponentInfo } from '@/lib/cms-types'
import { Home, User, Settings, Mail, Square } from 'lucide-react'

export const metadata: ComponentInfo = {
  type: 'Navigation',
  name: 'Brutalist Navigation',
  description: 'Bold navigation with brutalist design',
  category: 'layout',
  icon: 'Menu',
}

export default function Navigation() {
  const [activeItem, setActiveItem] = useState('home')

  return (
    <nav className="w-full bg-black text-white border-b-4 border-theme-primary-500">
      <div className="theme-container">
        <div className="flex items-center justify-center space-x-0">
          <a 
            href="/" 
            onClick={() => setActiveItem('home')}
            className={`px-8 py-6 text-lg font-black uppercase tracking-wide transition-all border-r border-gray-700 ${
              activeItem === 'home' ? 'bg-theme-primary-500 text-black' : 'hover:bg-theme-primary-500 hover:text-black'
            }`}
          >
            {{NAV_ITEM_1}}
          </a>
          <a 
            href="/about" 
            onClick={() => setActiveItem('about')}
            className={`px-8 py-6 text-lg font-black uppercase tracking-wide transition-all border-r border-gray-700 ${
              activeItem === 'about' ? 'bg-theme-primary-500 text-black' : 'hover:bg-theme-primary-500 hover:text-black'
            }`}
          >
            {{NAV_ITEM_2}}
          </a>
          <a 
            href="/services" 
            onClick={() => setActiveItem('services')}
            className={`px-8 py-6 text-lg font-black uppercase tracking-wide transition-all border-r border-gray-700 ${
              activeItem === 'services' ? 'bg-theme-primary-500 text-black' : 'hover:bg-theme-primary-500 hover:text-black'
            }`}
          >
            {{NAV_ITEM_3}}
          </a>
          <a 
            href="/contact" 
            onClick={() => setActiveItem('contact')}
            className={`px-8 py-6 text-lg font-black uppercase tracking-wide transition-all ${
              activeItem === 'contact' ? 'bg-theme-primary-500 text-black' : 'hover:bg-theme-primary-500 hover:text-black'
            }`}
          >
            {{NAV_ITEM_4}}
          </a>
        </div>
      </div>
    </nav>
  )
}
